# with K allowed flips from 0 -> 1
def max_consecutive_with_k(A,K):
    left = right = 0
    for right in range(len(A)):
      # if we encounter a 0 the we decrement K
      if A[right] == 0:
        K -= 1
      # else no impact to K
    
      # if K < 0 then we need to move the left part of the window forward
      # to try and remove the extra 0's
      if K < 0:
        if A[left] == 0:
            K += 1
        left += 1
    print(right - left + 1)

def max_consecutive_without_k(A):
    print('test')
    max=0
    max_so_far=0
    for ele in A:
        if ele == 1:
            max_so_far+=1
            if max_so_far > max:
                max=max_so_far
        else:
            max_so_far=0
        print(f' ele : {ele} max_so_far: {max_so_far} max: {max}')

    print('max window : ', max)

